package com.example.enums;import java.util.Scanner;/** * This program enumerated typers * * @author cgz * @version at 2018/9/20 22:08 */public class EunmTest {	public static void main(String[] args) {		Scanner in = new Scanner(System.in);		System.out.println("enter a size:(SMALL,MEDIUM,LARGEï¼ŒEXTRA_LARGE)");		String input = in.next().toUpperCase();		Size size = Enum.valueOf(Size.class, input);		System.out.println("size=" + size);		System.out.println("abbreviation=" + size.getAbbreviation());		if (size == Size.EXTRA_LARGE){			System.out.println("Good job -- you pay attention to the _.");		}	}	enum Size {		SMALL("S"), MEDIUM("M"), LARGE("L"), EXTRA_LARGE("XL");		Size(String abbreviation) {			this.abbreviation = abbreviation;		}		private String abbreviation;		public String getAbbreviation() {			return abbreviation;		}	}}